Centauri.Utility.EditorUtility.getCustomHTMLByType = (inputObj) => {
    let html = "";
    let type = inputObj.custom;
    let data = inputObj.data;

    let fieldClassName = "field";
    let additionalFieldClasses = "";

    if(type == "select") {
        fieldClassName = "ci-field";

        let defaultLabel = "Choose an option";
        let label = (Centauri.isNotUndefined(data.label) ? data.label : defaultLabel);

        html = "<select class='ci-select' id='" + inputObj.id + "'>|</select>{LABEL_1}<i class='fas fa-chevron-down'></i>{LABEL_2}";

        let optionsHtml = "<option selected disabled>" + label + "</option>";
        if(Centauri.isNotUndefined(data.options)) {
            $.each(data.options, (index, optionObj) => {
                if(Centauri.isNotUndefined(data.selectedOptionValue)) {
                    if(data.selectedOptionValue == optionObj.value) {
                        optionsHtml += "<option value='" + optionObj.value + "' selected>" + optionObj.name;

                        html = html.replace("{LABEL_2}", "<label for='" + inputObj.id + "'>" + optionObj.name + "</label>");
                    } else {
                        optionsHtml += "<option value='" + optionObj.value + "'>" + optionObj.name;
                    }
                } else {
                    optionsHtml += "<option value='" + optionObj.value + "'>" + optionObj.name;
                }
            });
        }

        html = html.replace("{LABEL_1}", "<label for='" + inputObj.id + "'>" + label + "</label>");
        html = html.replace("{LABEL_2}", "<label for='" + inputObj.id + "'>" + defaultLabel + "</label>");

        html = html.split("|").join(optionsHtml);
    }

    if(type == "image") {
        fieldClassName = "ci-field";
        html = "<img src='" + data.src + "' class='img-fluid' style='width: 30px;' /><label style='transform: translateY(-25px) scale(.8);' class='active'>" + data.label + "</label>";
    }

    if(type == "checkbox") {
        let checked = "";

        if(Centauri.isNotUndefined(data.isChecked)) {
            if(data.isChecked) {
                checked = " checked";
            }
        }

        additionalFieldClasses = " custom-control custom-checkbox";
        html = "<input id='" + inputObj.id + "' class='custom-control-input' type='checkbox'" + checked + " /><label class='custom-control-label d-block m-0' for='" + inputObj.id + "'>" + data.label + "</label>";
    }

    if(type == "switch") {
        additionalFieldClasses = " ci-switch";

        let checked = "";
        let onClick = "";

        if(Centauri.isNotUndefined(data.isChecked)) {
            if(data.isChecked) {
                checked = " checked";
            }
        }

        if(Centauri.isNotUndefined(data.onClick)) {
            onClick = " onclick='" + data.onClick + "'";
        }

        html = "<label><input type='checkbox'" + checked + " id='" + inputObj.id + "'" + onClick + " /><span></span>" + data.label + "</label>";
    }

    if(type == "radio") {
        let items = data.items;

        additionalFieldClasses = " radio";

        if(Centauri.isNotUndefined(inputObj.additionalFieldClasses)) {
            additionalFieldClasses += " " + inputObj.additionalFieldClasses;
        }

        items.forEach(item => {
            html += "<div class='form-check'><input type='radio' class='form-check-input' name='" + inputObj.id + "' id='" + item.id + "'" + (item.isChecked ? " checked" : "") + "><label class='form-check-label' for='" + item.id + "'>" + item.label + "</label></div>";
        });
    }

    if(type == "textarea") {
        html += "<div class='ci-field'><textarea" + (Centauri.isNotUndefined(data.required) ? (data.required ? " required" : "") : "") + " id='" + inputObj.id + "' class='md-textarea form-control' rows='3'>" + (Centauri.isNotUndefined(data.value) ? data.value : "") + "</textarea><label" + (Centauri.isNotUndefined(data.value) && data.value.length > 0 ? " class='active'" : "") + " for='" + inputObj.id + "'>" + data.label + "</label></div>";
    }

    return "<div class='" + fieldClassName + "" + additionalFieldClasses + "'" + (Centauri.isNotUndefined(inputObj.extraAttr) ? " " + inputObj.extraAttr : "") + ">" + html + "</div>";
};

Centauri.Utility.ModalUtility = (title, description, options, callbacks) => {
    let html = "";
    let addHTMLFn = Centauri.Utility.ModalUtility.addHTML;

    let id = "modal";
    let modalSize = "";
    let closeclass = "danger";
    let saveclass = "success";

    let isDialog = true;
    let closeOnSave = true;
    let cached = true;

    if(Centauri.isNotUndefined(options)) {
        if(Centauri.isNotUndefined(options.id)) {
            id = "-" + options.id;
        }

        if(Centauri.isNotUndefined(options.cached)) {
            cached = options.cached;
        }

        if(Centauri.isNotUndefined(options.size)) {
            modalSize = " modal-" + options.size;
        }
        if(Centauri.isNotUndefined(options.isDialog)) {
            isDialog = options.isDialog;
        }
        if(Centauri.isNotUndefined(options.closeOnSave)) {
            closeOnSave = options.closeOnSave;
        }

        if(Centauri.isNotUndefined(options.close)) {
            if(Centauri.isNotUndefined(options.close.class)) {
                closeclass = options.close.class;
            }
        }

        if(Centauri.isNotUndefined(options.save)) {
            if(Centauri.isNotUndefined(options.save.class)) {
                saveclass = options.save.class;
            }
        }
    }

    let _showingCached = false;
    if(Centauri.elExists($("#modal" + id)) && cached) {
        _showingCached = true;
        Centauri.Modal($("#modal" + id), "show");

        if(Centauri.isNotUndefined(callbacks)) {
            if(Centauri.isNotUndefined(callbacks.showingCached)) {
                callbacks.showingCached();
            }
        }
    }

    html = "<div class='modal fade" + (!isDialog ? " layout-default" : "") + "' id='modal" + id + "' tabindex='-1' role='dialog' aria-labelledby='modal" + id + "-label' aria-hidden='true'>|</div>";
    html = addHTMLFn(html, "<div class='modal-dialog" + modalSize + "' role='document'>|</div>");
    html = addHTMLFn(html, "<div class='modal-content'>|</div>");
    html = addHTMLFn(html, "<div class='modal-header'>|</div>&&");
    html = addHTMLFn(html, "<h5 class='modal-title' id='modallabel'>" + title + "</h5>|");
    html = addHTMLFn(html, "<button type='button' class='close' data-type='close' aria-label='Close'>|</button>");
    html = addHTMLFn(html, "<span aria-hidden='true'>&times;</span>");
    html = addHTMLFn(html, "<div class='modal-body'>" + description + "</div>&&", "&&");
    html = addHTMLFn(html, "<div class='modal-footer'>|</div>", "&&");
    html = addHTMLFn(html, "<button type='button' data-type='save' class='btn btn-" + saveclass + " waves-effect waves-light mr-3'>" + options.save.label + "</button>&&");
    html = addHTMLFn(html, "<button type='button' data-type='close' class='btn btn-" + closeclass + " waves-effect waves-light'>" + options.close.label + "</button>", "&&");

    if(!_showingCached) {
        $("body").append(html);
        Centauri.Modal();
        CentauriJS.Utilities.Form.FieldHasValueUtility();
    }

    if(Centauri.isNotUndefined(callbacks)) {
        if(Centauri.isNotUndefined(callbacks.ready)) {
            callbacks.ready();
        }
    }

    $("#modal" + id + " button").off();
    $("#modal" + id + " button").on("click", this, function() {
        let btntype = $(this).data("type");

        if(btntype == "close") {
            Centauri.Modal("close");

            if(Centauri.isNotUndefined(callbacks.close)) {
                callbacks.close();
            }
        }

        if(btntype == "save") {
            if(closeOnSave) {
                Centauri.Modal("close");
            }

            callbacks.save();
        }
    });

    if(_showingCached) {
        return true;
    }

    Centauri.Utility.ModalUtility.Validator($("#modal"));

    // $("#modal" + id + " select.mdb-select.md-form").materialSelect();

    let selectedValue = $("#modal" + id + " select.mdb-select.md-form").val();
    let selectedValueText = $.trim($("#modal" + id + " select option[value='" + selectedValue + "']").text());

    $("#modal" + id + " select.mdb-select.md-form").parent().find("ul > li").each(function() {
        let thisText = $.trim($("span", this).text());

        if(thisText == selectedValueText) {
            $(this).addClass("active selected");
            return;
        }
    });

    /**
     * Giving form fields (e.g. input, selects etc.) unique id & for attributes
     */
    Centauri.fn.__FormInputFix();

    return true;
};

