/**
 * Example Usage:
 * > Centauri.fn.Modal("Hello...", "...world!", {id: "id", close: {label: "CLOOOSE"}, save: {label: "SAAAAVE"}}, {size: "lg"});
 * with Callback > Centauri.fn.Modal("Hello...", "...world!", {id: "id", close: {label: "Cancel",class: "warning"}, save: {label: "Delete",class: "danger"}}, {save() {}});
 */
Centauri.fn.Modal = (title, description, options, callbacks) => {
    return Centauri.Utility.ModalUtility(title, description, options, callbacks);
};

Centauri.Modal = (arg = null, $this) => {
    if(!Centauri.elExists($this) && Centauri.Utility.ModalUtility.AutofindModalIfNotProvided) {
        $this = $("body > .modal");
    }

    if(Centauri.isNotNull(arg)) {
        if(arg == "show") {
            $this.css("display", "block");
            $this.removeClass("hide");

            setTimeout(() => {
                $this.addClass("show");
            }, 660);
        }

        if(arg == "close") {
            $this.addClass("hide");
            $this.removeClass("show");

            setTimeout(() => {
                $this.remove();
            }, 750);
        }

        if(arg == "hide") {
            $this.addClass("hide");

            setTimeout(() => {
                $this.removeClass("show");
            }, 660);

            setTimeout(() => {
                $this.css("display", "none");
            }, 330);
        }
    } else {
        $this.css("display", "block");
        $this.addClass("hide");

        setTimeout(() => {
            $this.addClass("show");
            $this.removeClass("hide");

            $this.on("click", this, function(e) {
                if(!Centauri.elExists($(e.target).parents($(".modal-dialog")))) {
                    Centauri.Modal("close");
                }
            });
        }, 10);
    }
};

Centauri.Utility.ModalUtility.AutofindModalIfNotProvided = true;

Centauri.Utility.ModalUtility.Validator = function($modal) {
    var $input = $("input", $modal);
    var $select = $("select", $modal);

    $input.on("focusout", function() {
        var required = $(this).attr("required");

        if(Centauri.isNotUndefined(required)) {
            var value = $(this).val();

            if(value == "") {
                $(this).addClass("error");
            }
        }
    });

    $input.on("keyup", function() {
        var required = $(this).attr("required");

        if(Centauri.isNotUndefined(required)) {
            var value = $(this).val();

            if($(this).hasClass("error")) {
                if(value != "") {
                    $(this).removeClass("error");
                }
            } else {
                if(value == "") {
                    $(this).addClass("error");
                }
            }
        }
    });

    $select.on("change", function() {
        let required = $(this).attr("required");
        let $ul = $(this).prev();

        if(Centauri.isNotUndefined(required)) {
            if($("li", $ul).hasClass("active")) {
                $(this).parent().find("input").removeClass("error");
            }
        }
    });

    $("button[data-type='save']").click(function(e) {
        let hasError = false;

        $("input", $modal).each(function() {
            var val = $(this).val();
            var required = $(this).attr("required");

            if(val == "" && Centauri.isNotUndefined(required)) {
                hasError = true;

                if(!$(this).hasClass("error")) {
                    $(this).addClass("error");
                }
            }
        });

        $("select", $modal).each(function() {
            let required = $(this).attr("required");
            let $ul = $(this).prev();

            if(Centauri.isNotUndefined(required)) {
                if(!$("li", $ul).hasClass("active")) {
                    hasError = true;
                    $(this).parent().find("input").addClass("error");
                }
            }
        });

        if(hasError) {
            e.preventDefault();
            e.stopImmediatePropagation();
            e.stopPropagation();

            Centauri.Notify("error", "Form Validation", "Please fill out all fields!");
        }
    });
};

